import React, { useState } from 'react';
import AuthProvider from './auth/AuthProvider';
import AxiosInterceptor from './api/axiosInterceptor';
import Router from './Router';
import useAuth from './auth/useAuth';
import axiosInstance from './api/axiosInstance';

const ChangePasswordModal = () => {
  const { auth, login, logout } = useAuth();
  const [newPassword, setNewPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  if (!auth?.user?.first_login || (auth.user.role !== 'officer' && auth.user.role !== 'supervisor')) return null;

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      await axiosInstance.put(`/api/users/${auth.user.user_id}/password`, { password: newPassword });
      // Fetch user baru agar first_login = false
      const res = await axiosInstance.get(`/api/users/${auth.user.user_id}`);
      await login(auth.token, res.data);
      setSuccess(true);
      setTimeout(() => window.location.reload(), 1000);
    } catch (err) {
      setError(err.response?.data?.error || 'Gagal ganti password');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
      <div className="bg-white rounded-lg shadow-lg p-8 max-w-md w-full">
        <h2 className="text-xl font-semibold mb-4 text-center">Ganti Password Pertama Kali</h2>
        <form onSubmit={handleChangePassword} className="space-y-4">
          <input
            type="password"
            className="input w-full border px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Password Baru"
            value={newPassword}
            onChange={e => setNewPassword(e.target.value)}
            minLength={6}
            required
            autoFocus
          />
          {error && <div className="text-red-500 text-sm">{error}</div>}
          {success && <div className="text-green-600 text-sm">Password berhasil diganti!</div>}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            disabled={loading}
          >
            {loading ? 'Menyimpan...' : 'Simpan Password Baru'}
          </button>
        </form>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <ChangePasswordModal />
      <Router>
        <AxiosInterceptor />
      </Router>
    </AuthProvider>
  );
};

export default App;
