import React, { useState, useEffect } from 'react';
import Router from './Router';
import Sidebar from './components/Sidebar';
import Navbar from './components/Navbar';
import { useAuth } from './auth/useAuth';
import { useLocation } from 'react-router-dom';

const SIDEBAR_WIDTH = 256; // 64 * 4 (w-64)

const App = () => {
  const { auth } = useAuth();
  const location = useLocation();
  const showSidebar = auth && location.pathname !== '/login';
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 768);
  const isLogin = location.pathname === '/login';

  useEffect(() => {
    const handleResize = () => setIsDesktop(window.innerWidth >= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Push layout only on desktop
  const pushSidebar = showSidebar && sidebarOpen && !isLogin && isDesktop;

  return (
    <div className={`${isLogin ? 'h-screen' : 'min-h-screen'} bg-gray-50`}>
      {!isLogin && (
        <Navbar onHamburgerClick={() => setSidebarOpen(!sidebarOpen)} style={{
          marginLeft: pushSidebar ? SIDEBAR_WIDTH : 0,
          transition: 'margin-left 0.3s',
        }} />
      )}
      <div className={`flex${isLogin ? ' h-screen' : ' min-h-screen'}`}>
        {showSidebar && (
          <Sidebar open={sidebarOpen} onClose={() => setSidebarOpen(false)} pushLayout={pushSidebar} />
        )}
        <div
          className="flex-1 min-w-0"
          style={{
            marginLeft: pushSidebar ? SIDEBAR_WIDTH : 0,
            transition: 'margin-left 0.3s',
          }}
        >
          <Router />
        </div>
      </div>
    </div>
  );
};

export default App;
