import React, { useState } from 'react';
import { useAuth } from '../../auth/useAuth';

const POS_LOKASI_MAP = {
  'Terminal Protection': [
    'Chief Terminal Protection',
    'Ruang Tunggu',
    'Walking Patrol',
    'Mezzanine Domestik',
    'Kedatangan Domestik',
    'Akses Karyawan',
    'Building Protection',
    'CCTV',
  ],
  'Non-Terminal Protection': [
    'Main Gate',
    'Chief Non-Teminal Protection',
    'Patroli',
    'Kargo',
    'Papa November',
    'Pos Congot',
  ],
  'Screening': [
    'PSCP',
    'Level 4',
    'HBS',
    'SCP LAGs',
    'SCP Transit',
    'SSCP',
    'OOG',
  ],
};

const OfficerLocationForm = ({ lokasi, onBack }) => (
  <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 fade-in">
    <button onClick={onBack} className="mb-4 button bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300">Kembali</button>
    <div className="bg-white rounded-lg shadow p-8 w-full max-w-md text-center">
      <h2 className="text-xl font-semibold mb-4">Form {lokasi}</h2>
      <div className="text-gray-400">(Form untuk lokasi ini masih kosong)</div>
    </div>
  </div>
);

const OfficerDashboard = () => {
  const { auth } = useAuth();
  const userPos = auth?.user?.pos;
  const lokasiList = POS_LOKASI_MAP[userPos] || [];
  const [selectedLokasi, setSelectedLokasi] = useState(null);

  if (selectedLokasi) {
    return <OfficerLocationForm lokasi={selectedLokasi} onBack={() => setSelectedLokasi(null)} />;
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 fade-in">
      <h2 className="text-2xl font-bold mb-6">Officer Dashboard</h2>
      <h3 className="text-lg font-medium mb-4">Pos: {userPos || '-'}</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 w-full max-w-3xl">
        {lokasiList.length === 0 && <div className="col-span-full text-gray-400">Tidak ada lokasi untuk pos ini.</div>}
        {lokasiList.map(lokasi => (
          <button
            key={lokasi}
            onClick={() => setSelectedLokasi(lokasi)}
            className="card bg-white rounded-lg shadow p-6 flex flex-col items-center justify-center cursor-pointer hover:shadow-lg transition border border-gray-100 hover:border-blue-400"
          >
            <span className="text-lg font-semibold mb-2">{lokasi}</span>
            <span className="text-blue-500 text-xs">Lihat Form</span>
          </button>
        ))}
      </div>
    </div>
  );
};

export default OfficerDashboard;
