import React, { useState, useEffect } from 'react';
import LogBookList from '../logbook/LogBookList';
import axiosInstance from '../../api/axiosInstance';

const PegawaiList = ({ onRegister, refresh }) => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    axiosInstance.get('/api/users')
      .then(res => {
        setUsers(res.data.filter(u => u.role === 'officer' || u.role === 'supervisor'));
        setError(null);
      })
      .catch(err => setError('Gagal mengambil data user'))
      .finally(() => setLoading(false));
  }, [refresh]);

  return (
    <div>
      <h2>Daftar Pegawai</h2>
      <button onClick={onRegister}>Register User</button>
      {loading && <div>Loading...</div>}
      {error && <div style={{color:'red'}}>{error}</div>}
      <ul>
        {users.map(user => (
          <li key={user.user_id}>{user.nama_lengkap} - {user.role} - {user.pos}</li>
        ))}
      </ul>
    </div>
  );
};

const RegisterUserForm = ({ onBack, onSuccess }) => {
  const [form, setForm] = useState({
    nama_lengkap: '',
    email: '',
    password: '',
    role: 'officer',
    pos: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      await axiosInstance.post('/api/users/register', form);
      onSuccess();
    } catch (err) {
      setError(err.response?.data?.error || 'Gagal register user');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Register User</h2>
      <form onSubmit={handleSubmit}>
        <input name="nama_lengkap" placeholder="Nama Lengkap" value={form.nama_lengkap} onChange={handleChange} required />
        <input name="email" placeholder="Email" value={form.email} onChange={handleChange} required />
        <input name="password" placeholder="Password" type="password" value={form.password} onChange={handleChange} required />
        <select name="role" value={form.role} onChange={handleChange} required>
          <option value="officer">Officer</option>
          <option value="supervisor">Supervisor</option>
        </select>
        <input name="pos" placeholder="Pos" value={form.pos} onChange={handleChange} required />
        <button type="submit" disabled={loading}>{loading ? 'Registering...' : 'Register'}</button>
        <button type="button" onClick={onBack}>Kembali</button>
      </form>
      {error && <div style={{color:'red'}}>{error}</div>}
    </div>
  );
};

const SuperAdminDashboard = () => {
  const [page, setPage] = useState('main');
  const [refreshPegawai, setRefreshPegawai] = useState(0);

  if (page === 'logbook') return <div><button onClick={() => setPage('main')}>Kembali</button><LogBookList /></div>;
  if (page === 'pegawai') return <div><button onClick={() => setPage('main')}>Kembali</button><PegawaiList onRegister={() => setPage('register')} refresh={refreshPegawai} /></div>;
  if (page === 'register') return <RegisterUserForm onBack={() => setPage('pegawai')} onSuccess={() => { setPage('pegawai'); setRefreshPegawai(r => r+1); }} />;

  return (
    <div style={{ display: 'flex', gap: 32 }}>
      <div style={{ border: '1px solid #ccc', padding: 32, cursor: 'pointer' }} onClick={() => setPage('logbook')}>
        <h2>Log Book</h2>
        <p>Lihat semua log book/laporan</p>
      </div>
      <div style={{ border: '1px solid #ccc', padding: 32, cursor: 'pointer' }} onClick={() => setPage('pegawai')}>
        <h2>Pegawai</h2>
        <p>Lihat dan kelola data pegawai</p>
      </div>
    </div>
  );
};

export default SuperAdminDashboard;
