import React, { useState } from 'react';
import axiosInstance from '../../api/axiosInstance';

const LogbookHarianInput = () => {
  const [formData, setFormData] = useState({
    date: '',
    lokasi: '',
    pos: '',
    shift: 'pagi',
    user_pembuat: '',
    user_penerima: '',
    user_supervisor: '',
    ttd_pembuat: '',
    ttd_penerima: '',
    ttd_supervisor: '',
    status: 'draft',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/laporan', formData);
      setSuccess('Form submitted successfully!');
      setFormData({
        date: '',
        lokasi: '',
        pos: '',
        shift: 'pagi',
        user_pembuat: '',
        user_penerima: '',
        user_supervisor: '',
        ttd_pembuat: '',
        ttd_penerima: '',
        ttd_supervisor: '',
        status: 'draft',
      });
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-2 sm:px-6 flex flex-col items-stretch">
      <div className="w-full max-w-2xl bg-white rounded-xl shadow-lg mx-auto p-6 sm:p-8">
        <h2 className="text-xl sm:text-2xl font-bold mb-6 text-blue-900 text-left">Logbook Harian</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">Tanggal</label>
              <input type="date" name="date" value={formData.date} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">Lokasi</label>
              <input type="text" name="lokasi" value={formData.lokasi} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">Pos</label>
              <input type="text" name="pos" value={formData.pos} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">Shift</label>
              <select name="shift" value={formData.shift} onChange={handleChange} className="border px-3 py-2 rounded w-full">
                <option value="pagi">Pagi</option>
                <option value="siang">Siang</option>
                <option value="malam">Malam</option>
              </select>
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">User Pembuat</label>
              <input type="text" name="user_pembuat" value={formData.user_pembuat} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">User Penerima</label>
              <input type="text" name="user_penerima" value={formData.user_penerima} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">User Supervisor</label>
              <input type="text" name="user_supervisor" value={formData.user_supervisor} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex items-center">
              <label className="block font-medium w-32 text-gray-700 mr-2">Status</label>
              <select name="status" value={formData.status} onChange={handleChange} className="border px-3 py-2 rounded w-full">
                <option value="draft">Draft</option>
                <option value="submit to supervisor">Submit to Supervisor</option>
                <option value="done">Done</option>
              </select>
            </div>
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mt-2">
            <div className="flex flex-col">
              <label className="block font-medium text-gray-700 mb-1">TTD Pembuat (base64)</label>
              <textarea name="ttd_pembuat" value={formData.ttd_pembuat} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex flex-col">
              <label className="block font-medium text-gray-700 mb-1">TTD Penerima (base64)</label>
              <textarea name="ttd_penerima" value={formData.ttd_penerima} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
            </div>
            <div className="flex flex-col">
              <label className="block font-medium text-gray-700 mb-1">TTD Supervisor (base64)</label>
              <textarea name="ttd_supervisor" value={formData.ttd_supervisor} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
            </div>
          </div>
          <div className="flex justify-end mt-6">
            <button type="submit" className="bg-blue-600 text-white px-6 py-2 rounded font-semibold shadow hover:bg-blue-700 transition">Submit</button>
          </div>
          {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
          {success && <p className="text-green-600 text-sm mt-2">{success}</p>}
        </form>
      </div>
    </div>
  );
};

export default LogbookHarianInput;
