import React, { useState } from 'react';
import Router from './Router';
import Sidebar from './components/Sidebar';
import Navbar from './components/Navbar';
import { useAuth } from './auth/useAuth';
import { useLocation } from 'react-router-dom';

const App = () => {
  const { auth } = useAuth();
  const location = useLocation();
  const showSidebar = auth && location.pathname !== '/login';
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const isLogin = location.pathname === '/login';

  return (
    <div className={`${isLogin ? 'h-screen' : 'min-h-screen'} bg-gray-50`}>
      {!isLogin && <Navbar onHamburgerClick={() => setSidebarOpen(true)} />}
      <div className={`flex${isLogin ? ' h-screen' : ' min-h-screen'}`}>
        {showSidebar && <Sidebar open={sidebarOpen} onClose={() => setSidebarOpen(false)} />}
        <div className="flex-1 min-w-0">
          <Router />
        </div>
      </div>
    </div>
  );
};

export default App;
