import React, { useState } from 'react';
import axiosInstance from '../../api/axiosInstance';
import useAuth from '../../auth/useAuth';

const BukuPengunjungCCTVInput = () => {
  const { auth } = useAuth();
  const [formData, setFormData] = useState({
    tanggal: '',
    nama_petugas: '',
    nama_pengunjung: '',
    instansi: '',
    no_telp: '',
    keterangan: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/buku-pengunjung-cctv', { ...formData, user_id: auth.user.user_id });
      setSuccess('Form submitted successfully!');
      setFormData({
        tanggal: '',
        nama_petugas: '',
        nama_pengunjung: '',
        instansi: '',
        no_telp: '',
        keterangan: '',
      });
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white rounded shadow space-y-4">
      <div>
        <label className="block font-medium mb-1">Tanggal</label>
        <input type="date" name="tanggal" value={formData.tanggal} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Nama Petugas</label>
        <input type="text" name="nama_petugas" value={formData.nama_petugas} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Nama Pengunjung</label>
        <input type="text" name="nama_pengunjung" value={formData.nama_pengunjung} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Instansi</label>
        <input type="text" name="instansi" value={formData.instansi} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">No. Telp</label>
        <input type="text" name="no_telp" value={formData.no_telp} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Keterangan</label>
        <textarea name="keterangan" value={formData.keterangan} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
      </div>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      {error && <p className="text-red-500 text-sm">{error}</p>}
      {success && <p className="text-green-600 text-sm">{success}</p>}
    </form>
  );
};

export default BukuPengunjungCCTVInput;
