import React, { useState } from 'react';
import axiosInstance from '../../api/axiosInstance';

const LogbookPatroliRandomInput = () => {
  const [formData, setFormData] = useState({
    jam: '',
    checkpoint: '',
    temuan: '',
    tindak_lanjut: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/logbook-patroli-random', formData);
      setSuccess('Form submitted successfully!');
      setFormData({ jam: '', checkpoint: '', temuan: '', tindak_lanjut: '' });
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white rounded shadow space-y-4">
      <div>
        <label className="block font-medium mb-1">Jam</label>
        <input type="time" name="jam" value={formData.jam} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Checkpoint</label>
        <input type="text" name="checkpoint" value={formData.checkpoint} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Temuan</label>
        <textarea name="temuan" value={formData.temuan} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Tindak Lanjut</label>
        <textarea name="tindak_lanjut" value={formData.tindak_lanjut} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
      </div>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      {error && <p className="text-red-500 text-sm">{error}</p>}
      {success && <p className="text-green-600 text-sm">{success}</p>}
    </form>
  );
};

export default LogbookPatroliRandomInput;
