import React from 'react';
import { Routes, Route, Navigate, useParams } from 'react-router-dom';
import { useAuth } from './auth/useAuth';
import Navbar from './components/Navbar';
import OfficerDashboard from './components/dashboard/OfficerDashboard';
import SupervisorDashboard from './components/dashboard/SupervisorDashboard';
import SuperAdminDashboard from './components/dashboard/SuperAdminDashboard';
import LogBookList from './components/logbook/LogBookList';
import AddLogBook from './components/logbook/AddLogBook';
import UpdateLogBook from './components/logbook/UpdateLogBook';
import ActivitiesList from './components/activities/ActivitiesList';
import ReportsList from './components/reports/ReportsList';
import ViewReport from './components/reports/ViewReport';
import Login from './components/Login';

// Import semua form yang sudah ada
import LogbookHarianInput from './components/forms/LogbookHarianInput';
import LogbookRotasiPersonelInput from './components/forms/LogbookRotasiPersonelInput'; // dummy, akan dibuat
import PemeriksaanManualInput from './components/forms/PemeriksaanManualInput';
import PenitipanSenjataApiSelainPenumpangInput from './components/forms/PenitipanSenjataApiSelainPenumpangInput';
import LogbookExplosiveDetectorInput from './components/forms/LogbookExplosiveDetectorInput';
import WalkingPatrolTerminalInput from './components/forms/WalkingPatrolTerminalInput';
import BehaviorFormInput from './components/forms/BehaviorFormInput';
import SuspiciousFormInput from './components/forms/SuspiciousFormInput';
import DataTrackingCCTVInput from './components/forms/DataTrackingCCTVInput';
import BukuPengunjungCCTVInput from './components/forms/BukuPengunjungCCTVInput';
import LogbookPenggunaanSmartDoorGateInput from './components/forms/LogbookPenggunaanSmartDoorGateInput';
import LogbookPatroliRandomInput from './components/forms/LogbookPatroliRandomInput';
import LogbookRAInput from './components/forms/LogbookRAInput';
import KegiatanInput from './components/forms/KegiatanInput';
import InventarisInputForm from './components/forms/InventarisInputForm';
import FormPiInput from './components/forms/FormPiInput';
import RekonsiliasiInput from './components/forms/RekonsiliasiInput';
import KemajuanPersonelInput from './components/forms/KemajuanPersonelInput';
import LogbookSterilisasiRuangTungguInput from './components/forms/LogbookSterilisasiRuangTungguInput';
import DailyCheckPanicButtonInput from './components/forms/DailyCheckPanicButtonInput';
import UnattendedBaggageInput from './components/forms/UnattendedBaggageInput';
import CheckListPemeriksaanRandomUnpredictableInput from './components/forms/CheckListPemeriksaanRandomUnpredictableInput';

// Routes are now open for testing with dummy data
const ProtectedRoute = ({ children }) => {
  const { auth } = useAuth();
  if (!auth) return <Navigate to="/login" />;
  return children;
};

const RoleBasedRoute = ({ children, allowedRoles }) => {
  const { auth } = useAuth();
  if (!auth) return <Navigate to="/login" />;
  if (!allowedRoles.includes(auth.user.role)) return <Navigate to="/dashboard" />;
  return children;
};

// Dummy untuk form yang belum ada
const DummyForm = ({ name }) => (
  <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 fade-in">
    <div className="bg-white rounded-lg shadow p-8 w-full max-w-md text-center">
      <h2 className="text-xl font-semibold mb-4">Form {name.replace(/-/g, ' ')}</h2>
      <div className="text-gray-400">(Form ini belum tersedia)</div>
    </div>
  </div>
);

// Mapping slug ke komponen form
const formComponentMap = {
  'logbook-harian': LogbookHarianInput,
  'logbook-rotasi-personel': LogbookRotasiPersonelInput, // dummy
  'pemeriksaan-manual': PemeriksaanManualInput,
  'penitipan-senjata-api-selain-penumpang': PenitipanSenjataApiSelainPenumpangInput,
  'logbook-explosive-detector': LogbookExplosiveDetectorInput,
  'walking-patrol-terminal': WalkingPatrolTerminalInput,
  'behavior-form': BehaviorFormInput,
  'suspicious-form': SuspiciousFormInput,
  'data-tracking-cctv': DataTrackingCCTVInput,
  'buku-pengunjung-cctv': BukuPengunjungCCTVInput,
  'logbook-penggunaan-smart-door-gate': LogbookPenggunaanSmartDoorGateInput,
  'logbook-patroli-random': LogbookPatroliRandomInput,
  'logbook-ra': LogbookRAInput,
  'kegiatan': KegiatanInput,
  'inventaris-input': InventarisInputForm,
  'form-pi': FormPiInput,
  'rekonsiliasi': RekonsiliasiInput,
  'kemajuan-personel': KemajuanPersonelInput,
  'logbook-sterilisasi-ruang-tunggu': LogbookSterilisasiRuangTungguInput,
  'daily-check-panic-button': DailyCheckPanicButtonInput,
  'unattended-baggage': UnattendedBaggageInput,
  'check-list-pemeriksaan-random-unpredictable': CheckListPemeriksaanRandomUnpredictableInput,
  // ... tambahkan mapping lain jika ada
};

// Wrapper untuk render form dinamis
function FormRouteWrapper() {
  const { formName } = useParams();
  const FormComponent = formComponentMap[formName] || (() => <DummyForm name={formName} />);
  return <FormComponent />;
}

const Router = () => {
  const { auth } = useAuth();

  return (
    <div className="App">
      <Navbar />
      <Routes>
        {/* Public routes - Show login page */}
        <Route path="/login" element={<Login />} />

        {/* Protected routes */}
        <Route path="/" element={<Navigate to="/dashboard" />} />
        <Route
          path="/dashboard/officer"
          element={
            <RoleBasedRoute allowedRoles={['officer', 'supervisor', 'superadmin']}>
              <OfficerDashboard />
            </RoleBasedRoute>
          }
        />
        <Route
          path="/dashboard/supervisor"
          element={
            <RoleBasedRoute allowedRoles={['supervisor', 'superadmin']}>
              <SupervisorDashboard />
            </RoleBasedRoute>
          }
        />
        <Route
          path="/dashboard/superadmin"
          element={
            <RoleBasedRoute allowedRoles={['superadmin']}>
              <SuperAdminDashboard />
            </RoleBasedRoute>
          }
        />
        <Route
          path="/logbook"
          element={
            <ProtectedRoute>
              <LogBookList />
            </ProtectedRoute>
          }
        />
        <Route
          path="/logbook/add"
          element={
            <ProtectedRoute>
              <AddLogBook />
            </ProtectedRoute>
          }
        />
        <Route
          path="/logbook/edit/:id"
          element={
            <ProtectedRoute>
              <UpdateLogBook />
            </ProtectedRoute>
          }
        />
        <Route
          path="/activities"
          element={
            <ProtectedRoute>
              <ActivitiesList />
            </ProtectedRoute>
          }
        />
        <Route
          path="/reports"
          element={
            <ProtectedRoute>
              <ReportsList />
            </ProtectedRoute>
          }
        />
        <Route
          path="/reports/view/:id"
          element={
            <ProtectedRoute>
              <ViewReport />
            </ProtectedRoute>
          }
        />
        <Route
          path="/form/:formName"
          element={
            <ProtectedRoute>
              <FormRouteWrapper />
            </ProtectedRoute>
          }
        />

        {/* Catch all route */}
        <Route path="*" element={<Navigate to="/dashboard/officer" />} />
      </Routes>
    </div>
  );
};

export default Router;
