import React, { useState } from 'react';
import axiosInstance from '../../api/axiosInstance';
import useAuth from '../../auth/useAuth';

const BehaviorFormInput = () => {
  const { auth } = useAuth();
  const [formData, setFormData] = useState({
    jam: '',
    petugas: '',
    lokasi: '',
    tindak_lanjut_laporan: '',
    status: '',
    keterangan: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/behavior-form', { ...formData, user_id: auth.user.user_id });
      setSuccess('Form submitted successfully!');
      setFormData({
        jam: '',
        petugas: '',
        lokasi: '',
        tindak_lanjut_laporan: '',
        status: '',
        keterangan: '',
      });
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white rounded shadow space-y-4">
      <div>
        <label className="block font-medium mb-1">Jam</label>
        <input type="time" name="jam" value={formData.jam} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Petugas</label>
        <input type="text" name="petugas" value={formData.petugas} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Lokasi</label>
        <input type="text" name="lokasi" value={formData.lokasi} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Tindak Lanjut Laporan</label>
        <textarea name="tindak_lanjut_laporan" value={formData.tindak_lanjut_laporan} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Status</label>
        <input type="text" name="status" value={formData.status} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Keterangan</label>
        <textarea name="keterangan" value={formData.keterangan} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
      </div>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      {error && <p className="text-red-500 text-sm">{error}</p>}
      {success && <p className="text-green-600 text-sm">{success}</p>}
    </form>
  );
};

export default BehaviorFormInput;
