import React, { useState } from 'react';
import axiosInstance from '../../api/axiosInstance';

const InventarisInputForm = () => {
  const [formData, setFormData] = useState({
    laporan_id: '',
    nama_barang: '',
    jumlah: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/inventaris-input', formData);
      setSuccess('Form submitted successfully!');
      setFormData({ laporan_id: '', nama_barang: '', jumlah: '' });
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white rounded shadow space-y-4">
      <div>
        <label className="block font-medium mb-1">Laporan ID</label>
        <input type="number" name="laporan_id" value={formData.laporan_id} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Nama Barang</label>
        <input type="text" name="nama_barang" value={formData.nama_barang} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Jumlah</label>
        <input type="number" name="jumlah" value={formData.jumlah} onChange={handleChange} required className="border px-3 py-2 rounded w-full" min="1" />
      </div>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      {error && <p className="text-red-500 text-sm">{error}</p>}
      {success && <p className="text-green-600 text-sm">{success}</p>}
    </form>
  );
};

export default InventarisInputForm;
