import React, { useState } from 'react';
import axiosInstance from '../../api/axiosInstance';

const WalkingPatrolTerminalInput = () => {
  const [formData, setFormData] = useState({
    laporan_id: '',
    tanggal: '',
    nama: '',
    area: '',
    clear: false,
    catatan_penting: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
    setFormData({
      ...formData,
      [e.target.name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/walking-patrol-terminal', formData);
      setSuccess('Form submitted successfully!');
      setFormData({ laporan_id: '', tanggal: '', nama: '', area: '', clear: false, catatan_penting: '' });
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white rounded shadow space-y-4">
      <div>
        <label className="block font-medium mb-1">Laporan ID</label>
        <input type="number" name="laporan_id" value={formData.laporan_id} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Tanggal</label>
        <input type="date" name="tanggal" value={formData.tanggal} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Nama</label>
        <input type="text" name="nama" value={formData.nama} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div>
        <label className="block font-medium mb-1">Area</label>
        <input type="text" name="area" value={formData.area} onChange={handleChange} required className="border px-3 py-2 rounded w-full" />
      </div>
      <div className="flex items-center">
        <input type="checkbox" name="clear" checked={formData.clear} onChange={handleChange} className="mr-2" />
        <label className="font-medium">Clear</label>
      </div>
      <div>
        <label className="block font-medium mb-1">Catatan Penting</label>
        <textarea name="catatan_penting" value={formData.catatan_penting} onChange={handleChange} className="border px-3 py-2 rounded w-full" />
      </div>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      {error && <p className="text-red-500 text-sm">{error}</p>}
      {success && <p className="text-green-600 text-sm">{success}</p>}
    </form>
  );
};

export default WalkingPatrolTerminalInput;
