import React from 'react';
import { useAuth } from '../auth/useAuth';
import { useNavigate } from 'react-router-dom';

const Sidebar = ({ open, onClose, pushLayout }) => {
  const { auth, logout } = useAuth();
  const navigate = useNavigate();
  if (!auth) return null;
  const { user } = auth;

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // Push layout di desktop jika pushLayout true
  const sidebarClass = pushLayout
    ? 'static md:flex md:flex-col md:w-64 md:py-8 md:px-6 h-screen bg-white border-r shadow-md px-6 py-8 transition-all duration-300 z-50'
    : `fixed top-0 left-0 z-50 w-64 bg-white border-r shadow-md px-6 py-8 h-screen md:h-auto
        transform transition-transform duration-200
        ${open ? 'translate-x-0' : '-translate-x-full'}
        md:static md:translate-x-0 md:flex md:flex-col md:w-64 md:py-8 md:px-6
        ${open ? '' : 'md:block'}`;

  return (
    <>
      {/* Overlay for mobile */}
      {open && !pushLayout && (
        <div
          className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
          onClick={onClose}
        />
      )}
      {/* Sidebar */}
      <aside className={sidebarClass} style={{ transition: 'all 0.3s' }}>
        {/* Close button for mobile */}
        <button
          className="absolute top-4 right-4 md:hidden text-gray-500 hover:text-gray-700 text-2xl"
          onClick={onClose}
          aria-label="Tutup sidebar"
        >
          &times;
        </button>
        <div className="mb-8 flex flex-col items-center mt-6 md:mt-0">
          <div className="w-16 h-16 rounded-full bg-blue-100 flex items-center justify-center text-2xl font-bold text-blue-700 mb-2">
            {user.nama_lengkap ? user.nama_lengkap[0] : '?'}
          </div>
          <div className="text-lg font-semibold text-gray-800 text-center">{user.nama_lengkap}</div>
          <div className="text-sm text-blue-600 font-medium mt-1">{user.role}</div>
          {user.pos && <div className="text-xs text-gray-500 mt-1">{user.pos}</div>}
          {user.lokasi && <div className="text-xs text-gray-500 mt-1">{user.lokasi}</div>}
        </div>
        <button
          onClick={handleLogout}
          className="w-full bg-red-500 text-white py-2 rounded hover:bg-red-600 text-sm font-semibold shadow"
        >
          Logout
        </button>
      </aside>
    </>
  );
};

export default Sidebar;
