import React, { useState } from 'react';
import Router from './Router';
import Sidebar from './components/dashboard/Sidebar';
import { useAuth } from './auth/useAuth';
import { useLocation } from 'react-router-dom';

const App = () => {
  const { auth } = useAuth();
  const location = useLocation();
  const showSidebar = auth && location.pathname !== '/login';
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hamburger for mobile */}
      {showSidebar && (
        <button
          className="fixed top-3 left-3 z-50 md:hidden bg-white border shadow rounded-full p-2 focus:outline-none"
          onClick={() => setSidebarOpen(true)}
          aria-label="Buka sidebar"
        >
          <svg className="w-7 h-7 text-gray-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      )}
      <div className="flex">
        {showSidebar && <Sidebar open={sidebarOpen} onClose={() => setSidebarOpen(false)} />}
        <div className="flex-1 min-w-0">
          <Router />
        </div>
      </div>
    </div>
  );
};

export default App;
