import React, { useState } from 'react';
import { useAuth } from '../../auth/useAuth';
import { useNavigate } from 'react-router-dom';

// Mapping lokasi ke jenis logbook sesuai instruksi user
const POS_LOGBOOK_MAP = {
  'Screening': {
    'PSCP': [
      'logbook harian',
      'logbook rotasi personel',
      'pemeriksaan manual',
      'penitipan senjata api selain penumpang',
      'logbook explosive detector',
    ],
    'Level 4': [
      'logbook harian',
      'logbook rotasi personel',
    ],
    'HBS': [
      'logbook harian',
    ],
    'SCP LAGs': [
      'logbook harian',
    ],
    'SCP Transit': [
      'logbook harian',
      'logbook rotasi personel',
    ],
    'SSCP': [
      'logbook harian',
      'logbook rotasi personel',
      'logbook explosive detector',
      'form pi',
      'penitipan senjata api selain penumpang',
    ],
    'OOG': [
      'logbook harian',
      'logbook rotasi personel',
      'form pi',
      'rekonsiliasi',
      'penitipan senjata api selain penumpang',
    ],
  },
  'Terminal Protection': {
    'Chief Terminal Protection': [
      'logbook harian',
      'kemajuan personel',
    ],
    'Ruang Tunggu': [
      'logbook sterilisasi ruang tunggu',
      'logbook harian',
    ],
    'Walking Patrol': [
      'logbook harian',
      'walking patrol terminal',
    ],
    'Mezzanine Domestik': [
      'logbook harian',
    ],
    'Kedatangan Domestik': [
      'logbook harian',
    ],
    'Akses Karyawan': [
      'logbook harian',
    ],
    'Building Protection': [
      'logbook harian',
    ],
    'CCTV': [
      'logbook harian',
      'logbook penggunaan smart door gate',
      'daily check panic button',
      'unattended baggage',
      'behavior form',
      'suspicious form',
      'data tracking cctv',
      'buku pengunjung cctv',
    ],
  },
  'Non-Terminal Protection': {
    'Main Gate': [
      'logbook harian',
      'logbook rotasi personel',
      'check list pemeriksaan random & unpredictable',
    ],
    'Chief Non-Termina': [
      'logbook harian',
      'kemajuan personel',
    ],
    'Patroli': [
      'logbook harian airside',
      'logbook harian landside',
      'check list patroli airside',
      'check list patroli landside',
      'logbook random',
    ],
    'Kargo': [
      'logbook harian kargo domestik',
      'logbook harian kargo international',
      'logbook ra',
    ],
    'Papa November': [
      'logbook harian',
      'walking patrol landside',
    ],
    'Pos Congot': [
      'logbook harian',
    ],
  },
};

function slugify(str) {
  return str.toLowerCase().replace(/[^a-z0-9 ]/g, '').replace(/\s+/g, '-');
}

const OfficerDashboard = () => {
  const { auth } = useAuth();
  const userPos = auth?.user?.pos;
  const lokasiMap = POS_LOGBOOK_MAP[userPos] || {};
  const lokasiList = Object.keys(lokasiMap);
  const [openDropdown, setOpenDropdown] = useState(null);
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 fade-in">
      <h2 className="text-2xl font-bold mb-6">Officer Dashboard</h2>
      <h3 className="text-lg font-medium mb-4">Pos: {userPos || '-'}</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 w-full max-w-3xl">
        {lokasiList.length === 0 && <div className="col-span-full text-gray-400">Tidak ada lokasi untuk pos ini.</div>}
        {lokasiList.map(lokasi => (
          <div key={lokasi} className="relative">
            <button
              onClick={() => setOpenDropdown(openDropdown === lokasi ? null : lokasi)}
              className="card bg-white rounded-lg shadow p-6 flex flex-col items-center justify-center cursor-pointer hover:shadow-lg transition border border-gray-100 hover:border-blue-400 w-full"
            >
              <span className="text-lg font-semibold mb-2">{lokasi}</span>
              <span className="text-blue-500 text-xs">Pilih Jenis Logbook</span>
            </button>
            {openDropdown === lokasi && (
              <div className="absolute z-10 left-0 right-0 mt-2 bg-white border rounded shadow-lg p-2 animate-fade-in">
                {lokasiMap[lokasi].map(jenis => (
                  <button
                    key={jenis}
                    onClick={() => navigate(`/form/${slugify(jenis)}`)}
                    className="block w-full text-left px-4 py-2 hover:bg-blue-50 rounded text-gray-700"
                  >
                    {jenis}
                  </button>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default OfficerDashboard;
